mapper.update:
update tbl_product_img
    set pName=#{pName}, pThumbnail=#{pThumbnail}, pImage=#{pImage}, pImagePath=#{pImagePath}, pInformation=#{pInformation}, pInfoPath=#{pInfoPath}
        where pno=#{pno}

mapper.select:

select p.pno, p.pName, i.pImage, i.pImagePath from  tbl_product p
inner join tbl_product_img i
on p.pno = #{p.pno};
