mapper.update:
update tbl_memberInfo
    set password=#{password}, nickName=#{nickName}, email=#{email}, phoneNum=#{phoneNum}, address=#{address}
        where mno=#{mno}

mapper.insert:
insert into tbl_deleteMem (id, password, name, nickName, email, phoneNum, gender, SSNum, address, auth, grade)
    select id, password, name, nickName, email, phoneNum, gender, SSNum, address, auth, grade from tbl_memberInfo
        where mno=#{mno}
